import React from "react"
import starLogo from "/Users/advait/Desktop/gitpositories/scrimba/airbnb/airbnb-clone/src/images/star.png"


export default function Card(props) {
    let badgeText
    if (props.openSpots === 0) {
        badgeText = "SOLD OUT"
    } else if (props.location === "Online") {
        badgeText = "ONLINE"
    }
    return (
        <div className="card--main">
            {props.openSpots === 0 && <div className="card--badge">SOLD OUT</div>}
            <img className="card--photo" src={require(`../images/${props.img}`)} alt="card live host" ></img>
            <div className="card--desc">

                <div className="card--stats">
                    <img className="card--star" src={starLogo} alt="star"></img>
                    <span>{props.rating}</span>
                    <span className="gray">({props.reviewCount}) â€¢ </span>
                    <span className="gray">{props.location}</span>
                </div>

                <p className="card--title">{props.title}</p>
                <p className="card--price"><span className="bold">From ${props.price} / </span><span>person</span></p>

            </div>
        </div>
    )
}
